const API_CONFIG = {
  // In development, proxy handles the routing to localhost:5000
  // In production, this would be your deployed backend URL
  BASE_URL: process.env.NODE_ENV === 'production' 
    ? 'https://your-backend-domain.com/api'  // Your deployed backend
    : '/api',  // Uses proxy in development

  ENDPOINTS: {
    DECODE_VIN: '/vin/decode',
    SEARCH_DEALERS: '/dealers/search',
    DEALS: '/deals',
    AUTH: '/auth',
    LOGIN: '/auth/login',
    LOGOUT: '/auth/logout',
    USERS: '/users',
  }
};

export default API_CONFIG;
